module basis
  implicit none
contains
  function emsav(x, c) result(v) bind(c, name="c_emsav")
    use iso_c_binding
    implicit none

    real (c_double), dimension(1:21) :: x
    real (c_double), dimension(0:148) :: c
    real (c_double)                   :: v
    ! ::::::::::::::::::::
    real (c_double), dimension(0:148) :: p

    call bemsav(x, p)
    v = dot_product(p,c)

    return
  end function emsav

  subroutine bemsav(x, p) bind(c, name="c_bemsav")
    use iso_c_binding
    implicit none

    real (c_double), dimension(1:21), intent(in)  :: x
    real (c_double), dimension(0:148), intent(out) :: p
    ! ::::::::::::::::::::
    real (c_double), dimension(0:681) :: m

    call evmono(x, m)
    call evpoly(m, p)

    return
  end subroutine bemsav

