#include "c_basis_2_1_4_purify.h"

extern "C" void evpoly_2_1_4_purify(double* y, Eigen::Ref<Eigen::RowVectorXd> p) {
    p(0) = y[2] + y[1];
    p(1) = y[1]*y[2];
    p(2) = y[0]*y[2] + y[0]*y[1];
    p(3) = y[2]*y[2] + y[1]*y[1];
    p(4) = y[0]*y[1]*y[2];
    p(5) = y[1]*y[2]*y[2] + y[1]*y[1]*y[2];
    p(6) = y[0]*y[2]*y[2] + y[0]*y[1]*y[1];
    p(7) = y[0]*y[0]*y[2] + y[0]*y[0]*y[1];
    p(8) = y[2]*y[2]*y[2] + y[1]*y[1]*y[1];
    p(9) = y[0]*y[1]*y[2]*y[2] + y[0]*y[1]*y[1]*y[2];
    p(10) = y[0]*y[0]*y[1]*y[2];
    p(11) = y[1]*y[1]*y[2]*y[2];
    p(12) = y[1]*y[2]*y[2]*y[2] + y[1]*y[1]*y[1]*y[2];
    p(13) = y[0]*y[2]*y[2]*y[2] + y[0]*y[1]*y[1]*y[1];
    p(14) = y[0]*y[0]*y[2]*y[2] + y[0]*y[0]*y[1]*y[1];
    p(15) = y[0]*y[0]*y[0]*y[2] + y[0]*y[0]*y[0]*y[1];
    p(16) = y[2]*y[2]*y[2]*y[2] + y[1]*y[1]*y[1]*y[1];
}
